$(function() {
    $('a[href*=#]:not([href=#])').click(function() {
        if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
            var target = $(this.hash);
            target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top
                }, 1000, "easeInOutQuad");
                return false;
            }
        }
    });
});


function pulsate() {
    var pulser = $(".pulsate");
    if(!pulser.hasClass('stop')){
        pulser.delay(1000).animate({opacity: 0.2}, 2200, 'linear').delay(1000)
            .animate({opacity: 1}, 2200, 'linear', pulsate);
    }else{
        pulser.animate({opacity:1},2200)
            .removeClass('stop');
    }
}

$(document).ready(function() {
    pulsate();
    $('a').click(function(){
        $('.pulsate').hide();

    });
});

